import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';

import Layout from '../Templates/Layout';
import { getContacts, clearContacts } from '../redux/slices/contactsSlice';
import { getContact, clearContact } from '../redux/slices/contactSlice';


const HomePage = () => {
  const initialState = { nameGetContact: '' };
  const [data, setData] = useState(initialState);
  const [error, setError] = useState('');

  function getErrorFindContact(data) {
    if (data.nameGetContact == "") {
      return "Cannot be empty";
    }
    return "";
  }

  const ContactComponent = () => {
    return (
      true ? (
      <div className="container fixed-bg-3 text-center">
      <h1 className="text-center display-4 pb-5">message</h1> {/*{contact["message"]} */}
      <div className="fixed-bg-question">
          <h3>Name: </h3>
          <h3>Email:</h3>
          <h3>Gender: </h3>
          <h3>Phone:</h3>
          {/* <h3>Name: {contact.data.name}</h3>
          <h3>Email: {contact.data.email}</h3>
          <h3>Gender: {contact.data.gender}</h3>
          <h3>Phone: {contact.data.phone}</h3> */}
        </div>
      </div>
      ) : <h1>nothing</h1>
    );
  };






  const dispatch = useDispatch();
  const history = useHistory();

  // update  fields
  const handleChange = ({ target: { value, name } }) => {
    setData({ ...data, [name]: value });
  };

  function handleClickGetContacts() {
      // dispatch(clearContact());
      dispatch(getContacts());
      history.push("/allcontacts");
  };

  function handleClickGetContact() {
    const errorMsg = getErrorFindContact(data);
    if (!errorMsg) {
      setError('');

      console.log(data.nameGetContact);
      // dispatch(clearContact());
      dispatch(getContact(data.nameGetContact));
      history.push("/findcontact");

    } else {
      setError(errorMsg);
    }



};

  return (
    <Layout>
      <div className="container fixed-bg-3 text-center">
        <h1 className="text-center display-4 pb-5 pt-5">RestHub</h1>
        <Button
          variant="primary"
          size="lg"
          className="pp-button-long mb-5"
          onClick={handleClickGetContacts}
        >View All Contacts
        </Button>

        <form>
          <input
            className="input-top"
            name="nameGetContact"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={data.nameGetContact}
          />
          <p className="error-message">{error}</p>
        </form>
        <Button
          variant="primary"
          size="lg"
          className="pp-button"
          onClick={handleClickGetContact}
        >Find Contact
        </Button>

        <br />
      </div>

      <ContactComponent/>

      {/* <div className="container fixed-bg-1 text-center">
      </div> */}
    </Layout>
  )};

export default HomePage;
