import { createSlice } from '@reduxjs/toolkit';
import { API_HOST } from '../../consts';
import { loadState, removeState, saveState } from '../localStorage';

const CONTACT_STATE_KEY = 'contact';
const persistedContact = loadState(CONTACT_STATE_KEY);

export const contactSlice = createSlice({
  name: 'contact',
  initialState: persistedContact,
  reducers: {
    setContact: (state, action) => action.payload,
  },
});

export const { setContact } = contactSlice.actions;

export const getContact = (name) => (dispatch) => {
  const apiUrl = `${API_HOST}/api/contacts/${name}`; 
  fetch(apiUrl, {
    headers: {
      'Content-Type': 'application/json',
    },
    method: 'GET',
  })
    .then((response) => response.json())
    .then((result) => {
      console.log(result);

      dispatch(clearContact);
      saveState(CONTACT_STATE_KEY, result);
      dispatch(setContact(result));
      console.log(result);
    })
    .catch((err) => console.log(err));
};

export const clearContact = () => (dispatch) => {
  removeState(CONTACT_STATE_KEY);
  dispatch(setContact(null));
};

export const signupContact = (email, password, nickname) => (dispatch) => {
  fetch(`${API_HOST}/contact/contact`, {
    headers: {
      'Content-Type': 'application/json',
    },
    method: 'POST',
    body: JSON.stringify({ email, password, nickname }),
  })
    .then((response) => response.json())
    .then((result) => {
      if (result.status === 'success') {
        saveState(CONTACT_STATE_KEY, result.data);
        dispatch(setContact(result.data));
      } else {
        throw new Error(result.message);
      }
    })
    .catch((err) => console.log(err));
};

export const selectContact = (state) => state.contact;

export default contactSlice.reducer;
