import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';

import Layout from '../Templates/Layout';
import { getContacts, clearContacts } from '../redux/slices/contactsSlice';

import { API_HOST } from '../consts';



const HomePage = () => {
  const initialState = { topName: '', botName: '', botGender: '', botEmail: '', botPhone: '', botMessage: '', };
  const [myData, setMyData] = useState(initialState);
  const [error, setError] = useState('');

  const [name, setName] = useState('');
  const [gender, setGender] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [message, setMessage] = useState('');
  const [toggle, setToggle] = useState(0);



  function getErrorFindContact(myData) {
    if (myData.topName == "") {
      return "Cannot be empty";
    }
    return "";
  }

  const ContactComponent = () => {
    return (
      <div className="container fixed-bg-3 text-center">
      <h1 className="text-center display-4 pb-5 pt-5">{message}</h1> {/*{contact["message"]} */}

      {(toggle == 1) ? (
      <div className="fixed-bg-question">
          <h3>Name: {name}</h3>
          <h3>Email: {email}</h3>
          <h3>Gender: {gender}</h3>
          <h3>Phone: {phone}</h3>
        </div>
      ) : <div/>}

      </div>

    );
  };

  const dispatch = useDispatch();
  const history = useHistory();

  // update  fields
  const handleChange = ({ target: { value, name } }) => {
    setMyData({ ...myData, [name]: value });
  };

  // // update  fields
  // const updateMyData = (name, value) => {
  //   setMyData({ ...myData, [name]: value });
  // };

  function handleClickGetContacts() {
      // dispatch(clearContact());
      dispatch(getContacts());
      history.push("/allcontacts");
  };

  function resetFields() {
    setName("");  
    setEmail("");  
    setPhone("");  
    setGender(""); 
    setMessage("") 
    setToggle(0);

  }

  function handleClickGetContact() {
    resetFields();
    const errorMsg = getErrorFindContact(myData);
    if (!errorMsg) {
      setError('');
      const apiUrl = `${API_HOST}/api/contacts/${myData.topName}`; 
      fetch(apiUrl, {
        headers: {
          'Content-Type': 'application/json',
        },
        method: 'GET',
        })
          .then((response) => response.json())
          .then((result) => {
            setMessage(result.message);  
            if (result.data) {
              setToggle(1);
              setName(result.data.name);  
              setEmail(result.data.email);  
              setPhone(result.data.phone);  
              setGender(result.data.gender);  
            }

          })
          .catch((err) => console.log(err)
        );
      } else {
        setError(errorMsg);
      }
  };

  function handleClickDeleteContact() {
    resetFields();
    const errorMsg = getErrorFindContact(myData);
    if (!errorMsg) {
      setError('');
      const apiUrl = `${API_HOST}/api/contacts/${myData.topName}`; 
      fetch(apiUrl, {
        // headers: {
        //   'Content-Type': 'application/json',
        // },
        method: 'DELETE',
        })
          .then((response) => response.json())
          .then((result) => {
            // console.log(result);
            setMessage(result.message);              
          })
          .catch((err) => setMessage("Contact deleted successfully")
        );
      } else {
        setError(errorMsg);
      }
  };

  return (
    <Layout>
      <div className="container fixed-bg-3 text-center">
        <h1 className="text-center display-4 pb-5 pt-5">RestHub</h1>
        <Button
          variant="primary"
          size="lg"
          className="pp-button-long mb-5"
          onClick={handleClickGetContacts}
        >View All Contacts
        </Button>

        <form>
          <input
            className="input-top"
            name="topName"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.topName}
          />
          <p className="error-message">{error}</p>
        </form>
        <Button
          variant="primary"
          size="lg"
          className="pp-button"
          onClick={handleClickGetContact}
        >Find Contact
        </Button>
        <Button
          variant="primary"
          size="lg"
          className="pp-button ml-3"
          onClick={handleClickDeleteContact}
        >Delete Contact
        </Button>

        <form>
          <input
            className="input-top mt-5"
            name="topName"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.topName}
          />
          <br/>
          <input
            className="input-top"
            name="topName"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.topName}
          />
          <br/>
          <input
            className="input-top"
            name="topName"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.topName}
          />
          <br/>
          <input
            className="input-top"
            name="topName"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.topName}
          />
        </form>

        <br />
      </div>

      <ContactComponent/>

      {/* <div className="container fixed-bg-1 text-center">
      </div> */}
    </Layout>
  )};

export default HomePage;
