import React from 'react';
import { useSelector } from 'react-redux';
import Layout from '../Templates/Layout';
import { selectUser } from '../redux/slices/userSlice';

import HomePageLanding from './HomePageLanding';

// Icons made by <a href="https://www.flaticon.com/authors/srip" title="srip">srip</a> from <a href="https://www.flaticon.com/" title="Flaticon"> www.flaticon.com</a>

const HomePage = () => {
  const user = useSelector(selectUser);
  return (
    <Layout>
      {user ? (
        <div>
          <div className="container-fluid text-center vert-center-sm">
            <h1 className="display-4">Welcome, {user.nickname}</h1>
            <h3 className="pt-3 font-italic">We hope you will have a fulfilling experience with PeerPrep today!</h3>

            <div className="container-fluid text-center vert-center-sm">
              <div className="inner-flex-top">
                <div className="main-top">
                  {/* <img className="page-icon" src="/book.png" alt="How it works" /> */}
                  <div className="container fixed-bg text-center">
                    <h1 className="display-4 pb-5">PeerPrep</h1>
                  </div>
                </div>
                <div className="aside-top vert-center-md">
                  <h1>How it works</h1>
                  <p className="pt-1">Sign up first. Then we talk later.</p>
                </div>
              </div>
            </div>
          </div>

          {/* <h1>Welcome {user.nickname}</h1>  */}
        </div>

      ): <HomePageLanding />}
    </Layout>
  );
};

export default HomePage;
