import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';

import Layout from '../Templates/Layout';
import { getContacts, clearContacts } from '../redux/slices/contactsSlice';

import { API_HOST } from '../consts';



const HomePage = () => {
  const initialState = { nameGetContact: '', name: '', gender: '', email: '', phone: '', message: '', };
  const [myData, setMyData] = useState(initialState);
  const [error, setError] = useState('');

  function getErrorFindContact(myData) {
    if (myData.nameGetContact == "") {
      return "Cannot be empty";
    }
    return "";
  }

  const ContactComponent = () => {
    return (
      true ? (
      <div className="container fixed-bg-3 text-center">
      <h1 className="text-center display-4 pb-5">{myData.message}</h1> {/*{contact["message"]} */}
      <div className="fixed-bg-question">
          <h3>Name: {myData.name}</h3>
          <h3>Email: {myData.email}</h3>
          <h3>Gender: {myData.gender}</h3>
          <h3>Phone: {myData.phone}</h3>
        </div>
      </div>
      ) : <h1>nothing</h1>
    );
  };

  const dispatch = useDispatch();
  const history = useHistory();

  // update  fields
  const handleChange = ({ target: { value, name } }) => {
    setMyData({ ...myData, [name]: value });
  };

  // update  fields
  const updateMyData = (name, value) => {
    setMyData({ ...myData, [name]: value });
  };

  function handleClickGetContacts() {
      // dispatch(clearContact());
      dispatch(getContacts());
      history.push("/allcontacts");

      updateMyData("message", "result.message");  
      updateMyData("phone", "result.data.phone" || '');  
      updateMyData("name", "result.data.name" || '');  
      updateMyData("email", "result.data.email" || '');  
      updateMyData("gender", "result.data.gender" || '');  
  };

  function handleClickGetContact() {
    const errorMsg = getErrorFindContact(myData);
    let obj = {};
    if (!errorMsg) {
      setError('');

      // dispatch(clearContact());
      // dispatch(getContact(myData.nameGetContact));
      // history.push("/findcontact");
      const apiUrl = `${API_HOST}/api/contacts/${myData.nameGetContact}`; 
      fetch(apiUrl, {
        headers: {
          'Content-Type': 'application/json',
        },
        method: 'GET',
        })
          .then((response) => response.json())
          .then((result) => {
            obj = result;
            updateMyData("message", result.message);  
            if (result.data) {
              updateMyData("phone", result.data.phone || '');  
              updateMyData("name", result.data.name || '');  
              updateMyData("email", result.data.email || '');  
              updateMyData("gender", result.data.gender || '');  
            }

          })
          .catch((err) => console.log(err)
        );

        console.log(obj);



      } else {
        setError(errorMsg);
      }
};

  return (
    <Layout>
      <div className="container fixed-bg-3 text-center">
        <h1 className="text-center display-4 pb-5 pt-5">RestHub</h1>
        <Button
          variant="primary"
          size="lg"
          className="pp-button-long mb-5"
          onClick={handleClickGetContacts}
        >View All Contacts
        </Button>

        <form>
          <input
            className="input-top"
            name="nameGetContact"
            type="text"
            onChange={handleChange}
            placeholder="Contact Name (to find contact by name)"
            value={myData.nameGetContact}
          />
          <p className="error-message">{error}</p>
        </form>
        <Button
          variant="primary"
          size="lg"
          className="pp-button"
          onClick={handleClickGetContact}
        >Find Contact
        </Button>

        <br />
      </div>

      <ContactComponent/>

      {/* <div className="container fixed-bg-1 text-center">
      </div> */}
    </Layout>
  )};

export default HomePage;
