import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import Layout from '../Templates/Layout';
import * as Utils from '../utils';
import { loginUser } from '../redux/slices/userSlice';


const HomePage = () => {
  const initialState = { name: '', gender: '', email: '', phone: '' };
  const [data, setData] = useState(initialState);

  const dispatch = useDispatch();
  const history = useHistory();

  // update login fields
  const handleChange = (name, value) => {
    setData({ ...data, [name]: value });
  };

  const handleClick = () => {
    fetch('http://localhost:8080/api/contacts')
    .then(response => response.json())
    .then(res => {
      console.log(res.data);
      handleChange("name", res.data.name);
      handleChange("gender", res.data.gender);
      handleChange("email", res.data.email);
      handleChange("phone", res.data.phone);
    });
  };

  return (
    <Layout>
      <div className="container fixed-bg-1 text-center">
        <h1 className="text-center display-4 pb-5">RestHub</h1>
        <Button
          variant="primary"
          size="lg"
          className="pp-button-long"
          onClick={handleClick}
        >Get All Contacts
        </Button>

        <form>
          <input
            className="input-top"
            name="email"
            type="text"
            // onChange={handleChange}
            placeholder="Email"
            value={data.email}
          />
          <br />
          <input
            className="input-top"
            name="password"
            type="password"
            // onChange={handleChange}
            placeholder="Password"
            // value={loginData.password}
          />
        </form>

        <br />
      </div>

      <div className="container fixed-bg-1 text-center">
        {data}
      </div>
    </Layout>
  )};

export default HomePage;
